import { createContext, useContext, useState, useEffect } from 'react';

// Creamos el contexto de categorías
const CategoryContext = createContext();

// Componente Provider para envolver la aplicación
export const CategoryProvider = ({ children }) => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    // Usamos datos estáticos directamente
    const staticCategories = [
      { id: 1, name: 'Electrónica' },
      { id: 2, name: 'Computadoras' },
      { id: 3, name: 'Telefonía' },
      { id: 4, name: 'Accesorios' },
    ];
    setCategories(staticCategories); // Establecemos las categorías estáticas
  }, []);

  return (
    <CategoryContext.Provider value={{ categories }}>
      {children}
    </CategoryContext.Provider>
  );
};

// Custom hook para acceder a las categorías
export const useCategories = () => {
  const context = useContext(CategoryContext);
  if (!context) {
    throw new Error('useCategories debe ser usado dentro de un CategoryProvider');
  }
  return context;
};
