// src/components/NavBar.js
import { Link } from 'react-router-dom';
import { FaShoppingCart } from 'react-icons/fa';
import { useCart } from '../components/CartContext'; 
import { useCategories } from '../components/CategoryContext'; // Importa el hook de categorías
import './NavBar.css';

const NavBar = () => {
  const { getCartCount } = useCart();
  const categories = useCategories(); // Usar el hook para obtener las categorías

  return (
    <nav className="navbar">
      <Link to="/" className="brand">AZ TECH</Link>

      {/* Mostrar las categorías en la barra de navegación */}
      <ul className="categories">
        {categories.length > 0 ? (
          categories.map((category) => (
            <li key={category.id}>
              <Link to={`/category/${category.id}`} className="category-link">
                {category.name}
              </Link>
            </li>
          ))
        ) : (
          <p>Cargando categorías...</p>
        )}
      </ul>

      <div className="cart-icon">
        <Link to="/cart">
          <FaShoppingCart size={30} />
          <span className="cart-count">{getCartCount()}</span>
        </Link>
      </div>
    </nav>
  );
};

export default NavBar;
