import { createContext, useContext, useState, useEffect } from 'react';

// Creamos el contexto de categorías
const CategoryContext = createContext();

// Componente Provider para envolver la aplicación
export const CategoryProvider = ({ children }) => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    // Usamos datos estáticos directamente con productos dentro de cada categoría
    const staticCategories = [
      { 
        id: 1, 
        name: 'Electrónica', 
        products: [
          { id: 1, name: 'Procesador', description: 'Procesador de última generación', price: 150 }
        ]
      },
      { 
        id: 2, 
        name: 'Computadoras', 
        products: [
          { id: 2, name: 'Placa base', description: 'Placa base de alta calidad', price: 100 }
        ]
      },
      { 
        id: 3, 
        name: 'Telefonía', 
        products: [
          { id: 3, name: 'iPhone', description: 'iPhone 13 Pro Max', price: 999 }
        ]
      }
    ];
    setCategories(staticCategories); // Establecemos las categorías estáticas con productos
  }, []);

  return (
    <CategoryContext.Provider value={{ categories }}>
      {children}
    </CategoryContext.Provider>
  );
};

// Custom hook para acceder a las categorías
export const useCategories = () => {
  const context = useContext(CategoryContext);
  if (!context) {
    throw new Error('useCategories debe ser usado dentro de un CategoryProvider');
  }
  return context;
};
