// src/components/CategoryContext.js
import { createContext, useState, useEffect, useContext } from 'react';

const CategoryContext = createContext();

export const CategoryProvider = ({ children }) => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    // Usamos una API real para obtener las categorías
    const fetchCategories = async () => {
      try {
        const response = await fetch('https://miapi.com/categories'); // Aquí iría la URL de tu API
        const data = await response.json();
        setCategories(data); // Guardamos los datos obtenidos de la API
      } catch (error) {
        console.error('Error al cargar las categorías:', error);
      }
    };

    fetchCategories(); // Llamamos a la función para obtener las categorías
  }, []); // Solo se ejecuta una vez cuando el componente se monta

  return (
    <CategoryContext.Provider value={categories}>
      {children}
    </CategoryContext.Provider>
  );
};

export const useCategories = () => {
  return useContext(CategoryContext); // Devuelve las categorías
};
