// src/components/CategoryContext.js
import { createContext, useState, useEffect, useContext } from 'react';

// Creamos el contexto de categorías
const CategoryContext = createContext();

// Proveedor del contexto para envolver tu aplicación
export const CategoryProvider = ({ children }) => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    // Simulando la carga de categorías
    const fetchCategories = async () => {
      try {
        const response = await fetch('/api/categories'); // Ajusta la URL según tu API
        const data = await response.json();
        setCategories(data); // Guardamos las categorías en el estado
      } catch (error) {
        console.error('Error al cargar las categorías:', error);
      }
    };

    fetchCategories();
  }, []); // Solo se ejecuta una vez cuando el componente se monta

  return (
    <CategoryContext.Provider value={categories}>
      {children} {/* Proporciona las categorías a los componentes hijos */}
    </CategoryContext.Provider>
  );
};

// Hook personalizado para acceder a las categorías
export const useCategories = () => {
  return useContext(CategoryContext); // Devuelve las categorías almacenadas en el contexto
};
